                 break;                    case "base":                        config.options = options_base;                        break;                    default:                        config.options = options_doaj;                }                search_options.init("#filter-container", config.options);                config.options.dropdowns.forEach(function (entry) {                    if (typeof entry.width === "undefined") {                        entry.width = "110px";                    }                    search_options.select_multi('.dropdown_multi_' + entry.id, entry.name, entry.width)                })                var valueExists = function (key, value) {                    var find = config.options.dropdowns.filter(                            function (data) {                                return data[key] == value                            }                    );                    return (find.length > 0) ? (true) : (false);                }                if (valueExists("id", "time_range")) {                    if (config.service === "pubmed") {                        search_options.addDatePickerFromTo("#from", "#to", "any-time", "1809-01-01");                    } else if (config.service === "base") {                        search_options.addDatePickerFromTo("#from", "#to", "any-time", "1665-01-01");                    } else {                        search_options.addDatePickerFromTo("#from", "#to", "any-time", "1809-01-01");                    }                } else if (valueExists("id", "year_range")) {                    search_options.setDateRangeFromPreset("#from", "#to", "any-time-years", "1809");                }            }            var config = {};            $(document).ready(function () {                $('[data-toggle="popover"]').popover({trigger: "hover", placement: "top"});                                 var changeLibrary = function () {                    config.service = $("input[name='optradio']:checked").val();                    //var radio_val = $(this).val();                    //config.service = radio_val;                    $("#searchform").attr("action", "search?service=" + config.service);                    search_options.user_defined_date = false;                    $("#filter-container").html("");                                        $(".map-examples").css("display", "none");                    $(".map-examples." + config.service).css("display", "inline-block");                    chooseOptions();                };                                 $("input[name='optradio']").change(changeLibrary);                chooseOptions();                $("#searchform").attr("action", "search?service=" + config.service);                                changeLibrary();                                $("#searchterm").focus();            })                        $("#searchform").submit(function () {                var ua = window.navigator.userAgent;                var iOS = !!ua.match(/iPad/i) || !!ua.match(/iPhone/i);                var webkit = !!ua.match(/WebKit/i);                var iOSSafari = iOS && webkit && !ua.match(/CriOS/i);                                if(iOSSafari) {                    $("#searchform").attr("target", "");                }                            })                    </script>